import React from 'react';
import { Paper, Typography, Box } from '@mui/material';
import CardBackground from './CardBackground';
import { useNavigate } from 'react-router-dom';

const Card = ({ title, content, size, image, id }) => {
  const navigate = useNavigate();

  const getCardStyle = () => {
    switch (size) {
      case 'large':
        return { width: '100%', height: '50vh' };
      case 'medium':
        return { width: '100%', height: '30vh' };
      default:
        return { width: '100%', height: '20vh' };
    }
  };

  const handleClick = () => {
    navigate(`/receta/${id}`); // Navigate to the recipe detail page
  };

  return (
    <Paper
      elevation={3}
      style={{
        ...getCardStyle(),
        margin: '8px auto',
        borderRadius: '16px',
        overflow: 'hidden',
        position: 'relative',
        color: '#e09504',
      }}
      onClick={handleClick} // Add click event
    >
      <CardBackground image={image} size={size} />

      <Box
        style={{
          position: 'relative',
          zIndex: 2,
          padding: '16px',
          backdropFilter: 'blur(3px)',
          height: '100%',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'space-between',
        }}
      >
        {/* Fixed size container for the image */}
        {size === 'large' && image && (
          <Box
            style={{
              width: '100%',
              height: '50%', // Fixed height for image container
              overflow: 'hidden', // Hide overflow to prevent image size change
            }}
          >
            <Box
              component="img"
              src={image}
              alt={`${title} image`}
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover', // Scale the image to cover the container
                borderRadius: '8px',
                margin: '8px 0',
              }}
            />
          </Box>
        )}

        {size === 'medium' && image && (
          <Box
            style={{
              width: '75%', // Fixed width for image container
              height: '50%', // Fixed height for image container
              overflow: 'hidden', // Hide overflow to prevent image size change
              margin: '8px auto',
            }}
          >
            <Box
              component="img"
              src={image}
              alt={`${title} image`}
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover', // Scale the image to cover the container
                borderRadius: '8px',
              }}
            />
          </Box>
        )}

        <Box
          style={{
            marginTop: 'auto',
            paddingBottom: '10%',
          }}
        >
          <Typography
            variant="h6"
            component="h2"
            style={{
              color: 'white',
              fontFamily: 'Roboto, sans-serif', 
              fontWeight: 700, 
            }}
          >
            {title}
          </Typography>
          <Typography
            variant="body1"
            style={{
              color: 'white',
              fontFamily: 'Roboto, sans-serif',
              fontWeight: 400, 
            }}
          >
            {content}
          </Typography>
        </Box>
      </Box>
    </Paper>
  );
};

export default Card;
