import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Box, Typography } from '@mui/material';
import { usePalette } from 'color-thief-react';

const Receta = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);
  const [error, setError] = useState(null);
  const [bgColor, setBgColor] = useState('#ffffff'); // Default background color

  useEffect(() => {
    const fetchRecipeById = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/recipes/${id}`);
        setRecipe(response.data);
      } catch (err) {
        setError('Error fetching recipe');
        console.error(err);
      }
    };

    fetchRecipeById();
  }, [id]);

  // Get images only when recipe is available
  const images = recipe?.Images ? JSON.parse(recipe.Images.replace(/'/g, '"')) : [];
  const imageSrc = images[0] || ''; // Use the first image for color extraction

  // Use the usePalette hook to get the dominant color
  const { data: palette, error: paletteError } = usePalette(imageSrc);

  // Update background color based on the dominant color from the palette
  useEffect(() => {
    if (palette && palette.length > 0) {
      setBgColor(`rgb(${palette[0][0]}, ${palette[0][1]}, ${palette[0][2]})`);
    }
  }, [palette, imageSrc]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!recipe) {
    return <div>Loading...</div>;
  }

  // Parse instructions
  const instructions = recipe.RecipeInstructions ? JSON.parse(recipe.RecipeInstructions.replace(/'/g, '"')) : [];

  return (
    <Box
      sx={{
        height: '85vh',
        overflowY: 'auto',
        display: 'flex',
        flexDirection: 'column',
        backgroundColor: bgColor, // Apply background color to the main container
      }}
    >
      {/* Combined Image and Content Section */}
      <Box sx={{ flexGrow: 1 }}>
        {/* Image Section */}
        {imageSrc && (
          <Box
            component="img"
            src={imageSrc}
            alt={recipe.Name}
            sx={{
              width: '100%',
              height: '66.67%',
              objectFit: 'cover',
            }}
          />
        )}

        {/* Content Section */}
        <Box
          sx={{
            padding: '16px', // Padding for content
            backgroundColor: bgColor, // Same background color for the content area
            flexGrow: 1, // Allow the content to grow
          }}
        >
          <Typography variant="h6">{recipe.Name}</Typography> {/* Display recipe name */}
          <Typography variant="body1" sx={{ marginBottom: '16px' }}>
            {recipe.Description}
          </Typography>
          <Typography variant="h6" sx={{ marginBottom: '8px' }}>Instructions:</Typography>
          {instructions.map((step, index) => (
            <Typography key={index} variant="body1" component="p" sx={{ marginBottom: '8px', listStyleType: 'disc', marginLeft: '20px' }}>
              {"- " + step}
            </Typography>
          ))}
        </Box>
      </Box>
    </Box>
  );
};

export default Receta;
