import React from 'react';
import { Paper, Typography, Box } from '@mui/material';
import CardBackground from './CardBackground';
import { useNavigate } from 'react-router-dom';

const Card = ({ title, content, size, image, id }) => {
  const navigate = useNavigate();

  const getCardStyle = () => {
    switch (size) {
      case 'large':
        return { width: '100%', height: '50vh' };
      case 'medium':
        return { width: '100%', height: '30vh' };
      default:
        return { width: '100%', height: '20vh' };
    }
  };

  const handleClick = () => {
    navigate(`/receta/${id}`); // Navegar a la página de detalles de la receta
  };

  return (
    <Paper
      elevation={3}
      style={{
        ...getCardStyle(),
        margin: '8px auto',
        borderRadius: '16px',
        overflow: 'hidden',
        position: 'relative',
        color: '#e09504',
      }}
      onClick={handleClick} // Agregar evento de click
    >
      {/* Usar CardBackground para la imagen de fondo */}
      <CardBackground image={image} size={size} />

      {/* Ajustar la imagen para que ocupe todo el ancho en tamaños large y medium */}
      {['large', 'medium'].includes(size) && image && (
        <Box
          style={{
            width: '100%',
            height: size === 'large' ? '60%' : '40%', // Ajustar la altura de la imagen según el tamaño
            position: 'absolute',
            top: 0,
            zIndex: 1, // Mantener la imagen por detrás del contenido
          }}
        >
          <Box
            component="img"
            src={image}
            alt={`${title} image`}
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover', // Escalar la imagen para cubrir el contenedor
            }}
          />
        </Box>
      )}

      <Box
        style={{
          position: 'relative',
          zIndex: 2,
          padding: '4px 8px', // Reducir aún más el padding para acercar el texto a la imagen
          height: '100%',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'flex-end',
        }}
      >
        <Typography
          variant="h6"
          component="h2"
          style={{
            color: 'white',
            fontFamily: 'Roboto, sans-serif',
            fontWeight: 700,
            marginTop: '2px', // Reducir aún más el margen superior
          }}
        >
          {title}
        </Typography>
        <Typography
          variant="body1"
          style={{
            color: 'white',
            fontFamily: 'Roboto, sans-serif',
            fontWeight: 400,
            marginTop: '1px', // Reducir espacio entre título y contenido
          }}
        >
          {content}
        </Typography>
      </Box>
    </Paper>
  );
};

export default Card;
