import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Box, Typography } from '@mui/material';

const Receta = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRecipeById = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/recipes/${id}`);
        setRecipe(response.data);
      } catch (err) {
        setError('Error fetching recipe');
        console.error(err);
      }
    };

    fetchRecipeById();
  }, [id]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!recipe) {
    return <div>Loading...</div>;
  }

  // Parse images and instructions
  const images = recipe.Images ? JSON.parse(recipe.Images.replace(/'/g, '"')) : [];
  const imageSrc = images[0] || ''; // Use the first image for the blurred background
  const instructions = recipe.RecipeInstructions ? JSON.parse(recipe.RecipeInstructions.replace(/'/g, '"')) : [];

  return (
    <Box sx={{ height: '85vh', overflowY: 'auto', position: 'relative' }}>
      {/* Blurred Background Image */}
      {imageSrc && (
        <Box
          component="img"
          src={imageSrc}
          alt={recipe.Name}
          sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            filter: 'blur(8px)', // Adjust blur intensity here
            zIndex: -1, // Send the image to the back
          }}
        />
      )}

      {/* Content Section */}
      <Box
        sx={{
          padding: '16px',
          backgroundColor: 'rgba(255, 255, 255, 0.8)', // White background with slight transparency
          borderRadius: '8px', // Optional: Rounded corners
        }}
      >
        <Typography variant="h6">{recipe.Name}</Typography> {/* Display recipe name */}
        <Typography variant="body1" sx={{ marginBottom: '16px' }}>
          {recipe.Description}
        </Typography>
        <Typography variant="h6" sx={{ marginBottom: '8px' }}>Instructions:</Typography>
        {instructions.map((step, index) => (
          <Typography key={index} variant="body1" component="p" sx={{ marginBottom: '8px', listStyleType: 'disc', marginLeft: '20px' }}>
            {"- " + step}
          </Typography>
        ))}
      </Box>
    </Box>
  );
};

export default Receta;
