import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Box, Typography } from '@mui/material';

const Receta = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null); 
  const [error, setError] = useState(null); 

  useEffect(() => {
    const fetchRecipeById = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/recipes/${id}`); 
        setRecipe(response.data); 
      } catch (err) {
        setError('Error fetching recipe');
        console.error(err);
      }
    };

    fetchRecipeById();
  }, [id]); 

  if (error) {
    return <div>{error}</div>; 
  }

  if (!recipe) {
    return <div>Loading...</div>; 
  }

  return (
    <Box sx={{ height: '85vh', overflow: 'hidden', display: 'flex', flexDirection: 'column' }}>
      {/* Image Section */}
      <Box
        component="img"
        src={recipe.Images ? JSON.parse(recipe.Images.replace(/'/g, '"'))[0] : ''}
        alt={recipe.Name}
        sx={{
          width: '100%',
          height: '66.67%', // 2/3 of the PhoneFrame height
          objectFit: 'cover',
        }}
      />

      {/* Content Section */}
      <Box
        sx={{
          height: '33.33%', // 1/3 of the PhoneFrame height
          overflowY: 'auto', // Enable vertical scroll if content overflows
          padding: '16px', // Padding for content
          backgroundColor: '#fff', // Background color for the content area
        }}
      >
        <Typography variant="h6">{recipe.Name}</Typography> {/* Display recipe name */}
        <Typography variant="body1" sx={{ marginBottom: '16px' }}>
          {recipe.Description}
        </Typography> {/* Display recipe description */}
        {/* Additional details (e.g., steps) can be added here */}
      </Box>
    </Box>
  );
};

export default Receta;
