import React, { useEffect, useState } from 'react';
import { Box, Typography } from '@mui/material';
import Card from '../components/Card';
import axios from 'axios';

const MainScreen = () => {
  const [recipes, setRecipes] = useState([]);
  const [error, setError] = useState(null);
  const [scrollPosition, setScrollPosition] = useState(0); // Estado para almacenar la posición del scroll

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get('http://localhost:4000/feedRecipes');
        setRecipes(response.data.recipes); 
      } catch (err) {
        setError('Error fetching recipes');
        console.error(err);
      }
    };

    fetchRecipes();
  }, []);

  const getRandomSize = () => {
    const sizes = ['large', 'medium', 'small'];
    return sizes[Math.floor(Math.random() * sizes.length)];
  };

  const getRandomImage = (imagesString) => {
    const imagesArray = JSON.parse(imagesString.replace(/'/g, '"')); 
    return imagesArray.length > 0 ? imagesArray[Math.floor(Math.random() * imagesArray.length)] : 'default-image-url.jpg';
  };

  const getFirstSentence = (description) => {
    const sentenceEndings = /[.!?]/; 
    const firstSentence = description.split(sentenceEndings)[0];
    return firstSentence.trim() + (firstSentence ? '.' : ''); 
  };

  // Manejar el scroll para ocultar el mensaje
  useEffect(() => {
    const handleScroll = () => {
      setScrollPosition(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll); // Limpiar el evento al desmontar
  }, []);

  // Calcular la opacidad basada en la posición del scroll
  const opacity = Math.max(0, 1 - scrollPosition / 100); // Cambia 100 por el valor que desees para ajustar la rapidez del desvanecimiento

  return (
    <Box p={2} style={{ overflowY: 'auto', maxHeight: '100%' }}>
      <Typography 
        variant="h3" 
        style={{ 
          marginBottom: '16px', // Espacio entre el mensaje y la primera tarjeta
          padding: '8px 16px', 
          zIndex: 1000,
          color: 'white', 
          fontWeight: 'bold', 
          fontFamily: 'Roboto, sans-serif',
          transition: 'opacity 0.3s ease-in-out', // Transición suave para la opacidad
          opacity: opacity, // Aplicar la opacidad calculada
        }}
      >
         ¡Hola! ¿Qué cocinaremos hoy?
      </Typography>
      {recipes.map((recipe) => (
        <Card
          key={recipe.id}
          title={recipe.Name} 
          content={getFirstSentence(recipe.Description)} 
          size={getRandomSize()}
          image={getRandomImage(recipe.Images)} 
          id={recipe.id}
        />
      ))}
    </Box>
  );
};

export default MainScreen;
