import React from 'react';
import { Paper, Typography, Box } from '@mui/material';
import CardBackground from './CardBackground';
import { useNavigate } from 'react-router-dom';

const Card = ({ title, content, size, image, id }) => {
  const navigate = useNavigate();

  const getCardStyle = () => {
    switch (size) {
      case 'large':
        return { width: '100%', height: '50vh' };
      case 'medium':
        return { width: '100%', height: '30vh' };
      default:
        return { width: '100%', height: '20vh' };
    }
  };

  const handleClick = () => {
    navigate(`/receta/${id}`); // Navigate to the recipe detail page
  };

  return (
    <Paper
      elevation={3}
      style={{
        ...getCardStyle(),
        margin: '8px auto',
        borderRadius: '16px',
        overflow: 'hidden',
        position: 'relative',
        color: '#e09504',
      }}
      onClick={handleClick} // Add click event
    >
      <CardBackground image={image} size={size} />

      {image && (size === 'large' || size === 'medium') && (
        <Box
          style={{
            width: '100%', // Full width of the card
            height: size === 'large' ? '60%' : '40%', // 60% for large, 40% for medium
            overflow: 'hidden', // Prevent overflow
          }}
        >
          <Box
            component="img"
            src={image}
            alt={`${title} image`}
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover', // Scale the image to cover the container
              borderTopLeftRadius: '16px',
              borderTopRightRadius: '16px',
            }}
          />
        </Box>
      )}

      <Box
        style={{
          position: 'relative',
          zIndex: 2,
          padding: '16px',
          backdropFilter: 'blur(3px)',
          height: '100%',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'space-between',
        }}
      >
        <Box
          style={{
            marginTop: 'auto',
            paddingBottom: '10%',
          }}
        >
          <Typography
            variant="h6"
            component="h2"
            style={{
              color: 'white',
              fontFamily: 'Roboto, sans-serif', 
              fontWeight: 700, 
            }}
          >
            {title}
          </Typography>
          <Typography
            variant="body1"
            style={{
              color: 'white',
              fontFamily: 'Roboto, sans-serif',
              fontWeight: 400, 
            }}
          >
            {content}
          </Typography>
        </Box>
      </Box>
    </Paper>
  );
};

export default Card;
