import React from 'react';
import { Paper, Typography, Box } from '@mui/material';
import CardBackground from './CardBackground';
import { useNavigate } from 'react-router-dom';

const Card = ({ title, content, size, image, id }) => {
  const navigate = useNavigate();

  const getCardStyle = () => {
    switch (size) {
      case 'large':
        return { width: '100%', height: '50vh' };
      case 'medium':
        return { width: '100%', height: '30vh' };
      default:
        return { width: '100%', height: '20vh' };
    }
  };

  const handleClick = () => {
    navigate(`/receta/${id}`); // Navigate to the recipe detail page
  };

  return (
    <Paper
      elevation={3}
      style={{
        ...getCardStyle(),
        margin: '8px auto',
        borderRadius: '16px',
        overflow: 'hidden',
        position: 'relative',
        color: '#e09504',
      }}
      onClick={handleClick}
    >
      {/* Mostrar imagen a tamaño completo */}
      {['large', 'medium'].includes(size) && image && (
        <Box
          component="img"
          src={image}
          alt={`${title} image`}
          style={{
            width: '100%', // Ocupa todo el ancho de la card
            height: size === 'large' ? '70%' : '50%', // Ajusta la altura para ocupar más espacio
            objectFit: 'cover', // Escala la imagen para cubrir el contenedor
          }}
        />
      )}

      <Box
        style={{
          position: 'relative',
          zIndex: 2,
          padding: '16px',
          backgroundColor: 'rgba(0, 0, 0, 0.5)', // Fondo semi-transparente para mejor visibilidad del texto
          height: '100%',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'flex-end',
        }}
      >
        <Typography
          variant="h6"
          component="h2"
          style={{
            color: 'white',
            fontFamily: 'Roboto, sans-serif',
            fontWeight: 700,
          }}
        >
          {title}
        </Typography>
        <Typography
          variant="body1"
          style={{
            color: 'white',
            fontFamily: 'Roboto, sans-serif',
            fontWeight: 400,
          }}
        >
          {content}
        </Typography>
      </Box>
    </Paper>
  );
};

export default Card;
