import React from 'react';
import { Paper, Typography, Box } from '@mui/material';
import CardBackground from './CardBackground';
import { useNavigate } from 'react-router-dom';

const Card = ({ title, content, size, image, id }) => {
  const navigate = useNavigate();

  const getCardStyle = () => {
    switch (size) {
      case 'large':
        return { width: '100%', height: '50vh' };
      case 'medium':
        return { width: '100%', height: '30vh' };
      default:
        return { width: '100%', height: '20vh' };
    }
  };

  const handleClick = () => {
    navigate(`/receta/${id}`);
  };

  return (
    <Paper
      elevation={3}
      style={{
        ...getCardStyle(),
        margin: '8px auto',
        borderRadius: '16px',
        overflow: 'hidden',
        position: 'relative',
        color: '#e09504',
      }}
      onClick={handleClick}
    >
      {/* Card background */}
      <CardBackground image={image} size={size} />

      {/* Image container */}
      {['large', 'medium'].includes(size) && image && (
        <Box
          style={{
            width: '100%',
            height: size === 'large' ? '60%' : '40%',
            flexShrink: 0,
            position: 'relative', // Ensure image stays in layout flow
          }}
        >
          <Box
            component="img"
            src={image}
            alt={`${title} image`}
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover',
            }}
          />
        </Box>
      )}

      {/* Content box */}
      <Box
        style={{
          padding: '8px',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'flex-start',
          height: size === 'large' ? '40%' : '60%', // Adjust content height to share space
          position: 'relative', // Keep content in flow with image
        }}
      >
        <Typography
          variant="h6"
          component="h2"
          style={{
            color: 'white',
            fontFamily: 'Roboto, sans-serif',
            fontWeight: 700,
            marginTop: '4px', // Reduce margin for better layout
          }}
        >
          {title}
        </Typography>
        <Typography
          variant="body1"
          style={{
            color: 'white',
            fontFamily: 'Roboto, sans-serif',
            fontWeight: 400,
            marginTop: '2px', // Reduce space between title and content
          }}
        >
          {content}
        </Typography>
      </Box>
    </Paper>
  );
};

export default Card;
