import React from 'react';
import { Paper, Typography, Box } from '@mui/material';
import CardBackground from './CardBackground';
import { useNavigate } from 'react-router-dom';

const Card = ({ title, content, size, image, id }) => {
  const navigate = useNavigate();

  const getCardStyle = () => {
    switch (size) {
      case 'large':
        return { width: '100%', height: '50vh' };
      case 'medium':
        return { width: '100%', height: '30vh' };
      default:
        return { width: '100%', height: '20vh' };
    }
  };

  const handleClick = () => {
    navigate(`/receta/${id}`);
  };

  return (
    <Paper
      elevation={3}
      style={{
        ...getCardStyle(),
        margin: '8px auto',
        borderRadius: '16px',
        overflow: 'hidden',
        position: 'relative',
        cursor: 'pointer',
      }}
      onClick={handleClick}
    >
      {/* Image Section */}
      {image && (
        <Box
          component="img"
          src={image}
          alt={`${title} image`}
          style={{
            width: '100%', // Full width of the card
            height: size === 'large' ? '60%' : size === 'medium' ? '40%' : '100%', // Adjusted height for large and medium
            objectFit: 'cover', // Ensures image covers the container
          }}
        />
      )}

      {/* Content Section */}
      <Box
        style={{
          position: 'relative',
          zIndex: 2,
          padding: '16px',
          backgroundColor: 'rgba(0, 0, 0, 0.5)', // Semi-transparent background to enhance readability
          color: 'white',
          height: '100%',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'flex-end',
        }}
      >
        <Typography
          variant="h5" // Slightly larger title for better visibility
          component="h2"
          style={{
            fontFamily: 'Roboto, sans-serif',
            fontWeight: 700,
            marginBottom: '8px',
            textShadow: '1px 1px 2px black', // Adds a border-like effect around the text
          }}
        >
          {title}
        </Typography>
        <Typography
          variant="body2"
          style={{
            fontFamily: 'Roboto, sans-serif',
            fontWeight: 400,
          }}
        >
          {content}
        </Typography>
      </Box>
    </Paper>
  );
};

export default Card;
