import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Box, Typography } from '@mui/material';
import { ColorThief } from 'color-thief-react';

const Receta = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null); 
  const [error, setError] = useState(null); 
  const [bgColor, setBgColor] = useState('#ffffff'); // Default background color

  useEffect(() => {
    const fetchRecipeById = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/recipes/${id}`); 
        setRecipe(response.data); 
      } catch (err) {
        setError('Error fetching recipe');
        console.error(err);
      }
    };

    fetchRecipeById();
  }, [id]); 

  if (error) {
    return <div>{error}</div>; 
  }

  if (!recipe) {
    return <div>Loading...</div>; 
  }

  // Parse images and instructions
  const images = recipe.Images ? JSON.parse(recipe.Images.replace(/'/g, '"')) : [];
  const instructions = recipe.RecipeInstructions ? JSON.parse(recipe.RecipeInstructions.replace(/'/g, '"')) : [];

  const handleColorChange = (color) => {
    setBgColor(`rgb(${color[0]}, ${color[1]}, ${color[2]})`);
  };

  return (
    <Box sx={{ height: '85vh', overflowY: 'auto', display: 'flex', flexDirection: 'column', backgroundColor: bgColor }}>
      {/* Combined Image and Content Section */}
      <Box sx={{ flexGrow: 1, overflowY: 'auto' }}>
        {/* Image Section */}
        {images.length > 0 && (
          <ColorThief
            src={images[0]}
            crossOrigin="anonymous"
            onLoad={handleColorChange}
          >
            {({ color }) => (
              <Box
                component="img"
                src={images[0] || ''}
                alt={recipe.Name}
                sx={{
                  width: '100%',
                  height: '66.67%',
                  objectFit: 'cover',
                }}
              />
            )}
          </ColorThief>
        )}

        {/* Content Section */}
        <Box
          sx={{
            padding: '16px', // Padding for content
            backgroundColor: '#fff', // Background color for the content area
          }}
        >
          <Typography variant="h6">{recipe.Name}</Typography> {/* Display recipe name */}
          <Typography variant="body1" sx={{ marginBottom: '16px' }}>
            {recipe.Description}
          </Typography>
          <Typography variant="h6" sx={{ marginBottom: '8px' }}>Instructions:</Typography>
          {instructions.map((step, index) => (
            <Typography key={index} variant="body1" component="p" sx={{ marginBottom: '8px', listStyleType: 'disc', marginLeft: '20px' }}>
              {"- " + step}
            </Typography>
          ))}
        </Box>
      </Box>
    </Box>
  );
};

export default Receta;
