import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Receta = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null); // State to hold the fetched recipe
  const [error, setError] = useState(null); // State for error handling

  useEffect(() => {
    const fetchRecipeById = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/recipes/${id}`); // Fetch recipe by ID
        setRecipe(response.data); // Set the fetched recipe to state
      } catch (err) {
        setError('Error fetching recipe');
        console.error(err);
      }
    };

    fetchRecipeById();
  }, [id]); // Dependency array includes `id`

  if (error) {
    return <div>{error}</div>; // Show error if occurred
  }

  if (!recipe) {
    return <div>Loading...</div>; // Show loading while fetching
  }

  return (
    <div>
      <h1>{recipe.Name}</h1> {/* Display recipe name */}
      <p>{recipe.Description}</p> {/* Display recipe description */}
      <img src={recipe.Images ? JSON.parse(recipe.Images.replace(/'/g, '"'))[0] : ''} alt={recipe.Name} /> {/* Display first image */}
      {/* Show more details of the recipe if necessary */}
    </div>
  );
};

export default Receta;
