import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Box, Typography } from '@mui/material';

const Receta = () => {
  const { id } = useParams();
  const [recipe, setRecipe] = useState(null); 
  const [error, setError] = useState(null); 

  useEffect(() => {
    const fetchRecipeById = async () => {
      try {
        const response = await axios.get(`http://localhost:4000/recipes/${id}`); 
        setRecipe(response.data); 
      } catch (err) {
        setError('Error fetching recipe');
        console.error(err);
      }
    };

    fetchRecipeById();
  }, [id]); 

  if (error) {
    return <div>{error}</div>; 
  }

  if (!recipe) {
    return <div>Loading...</div>; 
  }

  // Parse images and instructions
  const images = recipe.Images ? JSON.parse(recipe.Images.replace(/'/g, '"')) : [];
  const instructions = recipe.RecipeInstructions ? JSON.parse(recipe.RecipeInstructions.replace(/'/g, '"')) : [];

  return (
    <Box sx={{ height: '85vh', overflowY: 'auto', display: 'flex', flexDirection: 'column' }}>
      {/* Combined Image and Content Section */}
      <Box sx={{ flexGrow: 1, overflowY: 'auto' }}>
        {/* Image Section */}
        <Box
          component="img"
          src={images[0] || ''}
          alt={recipe.Name}
          sx={{
            width: '100%',
            height: '66.67%',
            objectFit: 'cover',
            borderBottomLeftRadius: '16px', // Rounded bottom left corners
            borderBottomRightRadius: '16px', // Rounded bottom right corners
          }}
        />

        {/* Content Section */}
        <Box
          sx={{
            padding: '16px', // Padding for content
            backgroundColor: '#c48304',
            borderRadius: '16px', 
          }}
        >
          <Typography variant="h4" sx={{ color: 'white', marginBottom: '16px' }}>
  {recipe.Name}
</Typography>
          <Typography variant="body1" sx={{ marginBottom: '16px', color: '#3d2802' }}>
            {recipe.Description}
          </Typography>
          <Typography variant="h6" sx={{ marginBottom: '8px', color: '#3d2802' }}>Instructions:</Typography>
          {instructions.map((step, index) => (
            <Typography key={index} variant="body1" component="p" sx={{ marginBottom: '8px', listStyleType: 'disc', marginLeft: '20px', color: '#3d2802' }}>
              {"- " + step}
            </Typography>
          ))}
        </Box>
      </Box>
    </Box>
  );
};

export default Receta;
